'use strict';

/**
* Youtube tag
*
* Syntax:
*   {% youtube video_id %}
*/

function youtubeTag(args, content) {
  var id = args[0];

  return '<div class="video-container" style="position: relative; padding-bottom: 56.25%; padding-top: 25px; height: 0;"><iframe style="position:absolute; top:0; left:0; width: 100%; height: 100%;" src="//www.youtube.com/embed/' + id + '" frameborder="0" allowfullscreen></iframe></div>';
  // return '<div class="video-container" ><iframe src="//www.youtube.com/embed/' + id + '" frameborder="0" allowfullscreen></iframe></div>';
}

module.exports = youtubeTag;
